// src/pages/FormPage.js
import React, { useState } from 'react';
import axios from 'axios';

const FormPage = () => {
  // If you don't plan to use setFormData, you can remove it from the useState declaration
  const [formData] = useState({
    // Inisialisasi state formulir
    // ...
  });

  // The rest of your component remains the same
  const handleChange = (e) => {
    // Event handler untuk mengubah state formulir
    const { data} = e.target;
    // If setFormData was removed, you can directly use the state updater function in this context
    // setFormData((prevFormData) => ({
    //   ...prevFormData,
    //   [name]: value,
    // }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validasi formulir
    // ...

    try {
      // Kirim data formulir ke API menggunakan Axios
      const response = await axios.post('https://bit.ly/API-TEST', formData);
      console.log('API Response:', response.data);
      // Handle respons dari API
    } catch (error) {
      console.error('Error while submitting form:', error);
      // Handle error
    }
  };

  return (
    <div>
      <h2>Form Page</h2>
      <form onSubmit={handleSubmit}>
        {/* Example input field */}
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
          />
        </label>

        {/* Add more input fields as needed */}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default FormPage;
